mapper.select:
SELECT emp_id, ename, tel, job_name, dept_name
FROM emp e left join job j on e.job_id = j.job_id
           left join dept d on e.dept_id = d.dept_id
WHERE e.dept_id = #{deptId}
  and emp_id != 'admin';

SELECT emp_id, ename, tel, job_name, dept_name
FROM emp e left join job j on e.job_id = j.job_id
           left join dept d on e.dept_id = d.dept_id
WHERE e.ename like #{ename}
  and emp_id != 'admin';
