mapper.select:
SELECT chat_room_id, chat_room_name
FROM chat_room_member
WHERE user_id = #{userId}

mapper.update:
UPDATE chat_room_member
SET last_read_message_id = (SELECT MAX(chat_message_id)
                            FROM chat_message
                            WHERE chat_room_id = #{chatRoomId})
WHERE chat_room_id = #{chatRoomId}
  AND user_id = #{userId}

mapper.select:
SELECT * FROM chat_message WHERE chat_room_id = #{chatRoomId}
(#{chatRoomMember.chatRoomId}, #{chatRoomMember.userId})
ORDER BY chat_message_date ASC

SELECT user_id
FROM chat_room_member
WHERE chat_room_id = #{chatRoomId}

mapper.delete:
DELETE
FROM chat_room_member
WHERE chat_room_id = #{chatRoomId}
  AND user_id = #{userId}

mapper.update:
UPDATE chat_room_member
SET chat_room_name = #{chatRoomName}
WHERE chat_room_id = #{chatRoomId}
  AND user_id = #{userId}
